module Day2 where

import System.IO ( openFile, hGetContents, IOMode(ReadMode) )
import Data.List.Split (splitOn)
import Data.List (sort, intersect, nub)
import Data.Char

hasDuplicates :: (Ord a) => [a] -> Bool
hasDuplicates xs = length (nub xs) /= length xs

findMarker :: [Char] -> Int -> Int -> Int
findMarker xs factor depth = if (hasDuplicates $ take factor xs) then findMarker (tail xs) factor (depth + 1) else depth

main :: IO()
main = do 
    let s = ""
    print(findMarker s 4 4)
    print(findMarker s 14 14)